tinymce.PluginManager.add('ssinsertimg', function (editor) {

    function ssinsertimg() {
        var dom = editor.dom;

        $("#alertboxw2 iframe").attr("src","iframe_insertimg.html");
        $("#alertboxw2").show();

    }

    editor.addCommand("mceuploadrecipe", ssinsertimg);

    editor.addButton('ssinsertimg', {
        icon: 'image',
        tooltip: '插入图片',
        onclick: ssinsertimg
    });

    editor.addMenuItem('ssinsertimg', {
        icon: 'image',
        text: '插入图片',
        context: 'tools',
        onclick: ssinsertimg
    });
});