tinymce.PluginManager.add('ssinsertvideo', function (editor) {

    function ssinsertvideo() {


        $("#blackbg2").fadeIn();
        $("#alertbox2").fadeIn();

    }

    editor.addCommand("mceuploadrecipe", ssinsertvideo);

    editor.addButton('ssinsertvideo', {
        icon: 'media',
        tooltip: '插入视频',
        onclick: ssinsertvideo
    });

    editor.addMenuItem('ssinsertvideo', {
        icon: 'media',
        text: '插入视频',
        context: 'tools',
        onclick: ssinsertvideo
    });
});